// src/pages/InputPage.js (MUI 슬라이더 적용 버전)
import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';

const InputPage = ({ setPageState, setResultData }) => {
  // 각 슬라이더의 상태를 관리하기 위한 useState 훅
  const [crystalPrice, setCrystalPrice] = useState(8000); // 기본값 8000
  const [simulations, setSimulations] = useState(100);    // 기본값 100

  // 슬라이더 값이 변경될 때 호출되는 함수들
  const handleCrystalChange = (event, newValue) => {
    setCrystalPrice(newValue);
  };

  const handleSimulationsChange = (event, newValue) => {
    setSimulations(newValue);
  };

  const handleOptimizeClick = () => {
    // 나중에 API로 보낼 데이터를 콘솔에 출력하여 확인
    const requestData = {
      blue_crystal_price: crystalPrice,
      simulations_per_gem: simulations,
      // 코어와 젬 데이터는 나중에 추가
      cores: {},
      held_gems: []
    };
    console.log("API 요청 데이터:", requestData);

    setPageState('loading');
    setTimeout(() => {
      setResultData({ total_cost: 123456 });
      setPageState('result');
    }, 2000);
  };

  return (
    <div>
      <h2>1. 코어 구성</h2>
      <button>+ 코어 추가</button>

      <h2>2. 보유 젬 (선택)</h2>
      <button>+ 보유 젬 추가</button>

      <h2>3. 시장 및 설정</h2>
      {/* Box는 MUI에서 공간과 레이아웃을 잡기 위한 컴포넌트입니다. */}
      <Box sx={{ width: 300, margin: 'auto' }}>
        <Typography id="crystal-slider" gutterBottom>
          블루 크리스탈 시세: {crystalPrice.toLocaleString()} Gold
        </Typography>
        <Slider
          aria-labelledby="crystal-slider"
          value={crystalPrice}
          onChange={handleCrystalChange}
          valueLabelDisplay="auto"
          step={100}
          marks
          min={2000}
          max={20000}
        />
        <Typography id="simulations-slider" gutterBottom style={{ marginTop: '20px' }}>
          정밀도 (시뮬레이션 횟수): {simulations} 회
        </Typography>
        <Slider
          aria-labelledby="simulations-slider"
          value={simulations}
          onChange={handleSimulationsChange}
          valueLabelDisplay="auto"
          step={50}
          marks
          min={50}
          max={1000}
        />
      </Box>

      <hr style={{ marginTop: '30px' }}/>

      <button onClick={handleOptimizeClick} style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}>
        최적화 시작하기
      </button>
    </div>
  );
};

export default InputPage;
