// src/pages/InputPage.js (젬 시세 대시보드 적용 버전)
import React, { useState, useEffect } from 'react';
import { Box, Typography, Slider, Button, IconButton, Select, MenuItem, FormControl, InputLabel, TextField, Chip, Grid, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, CircularProgress } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';

const gemTypes = ["안정", "견고", "불변", "침식", "왜곡", "붕괴"];

const InputPage = ({ setPageState, setTaskId }) => {
  // --- State Hooks ---
  const [gemPrices, setGemPrices] = useState(null); // <<< 1. 젬 시세 데이터 state
  const [pricesLoading, setPricesLoading] = useState(true); // <<< 2. 시세 로딩 상태 state
  const [pricesError, setPricesError] = useState(null); // <<< 3. 시세 에러 상태 state

  const [cores, setCores] = useState([]);
  const [nextCoreId, setNextCoreId] = useState(1);
  const [heldGems, setHeldGems] = useState([]);
  const [nextGemId, setNextGemId] = useState(1);
  const [crystalPrice, setCrystalPrice] = useState(8000);
  const [simulations, setSimulations] = useState(100);

  // <<< 4. 컴포넌트가 처음 렌더링될 때 젬 시세 API를 호출하는 useEffect
  useEffect(() => {
    const fetchGemPrices = async () => {
      try {
        setPricesLoading(true);
        const response = await axios.get('http://localhost:8000/markets/gems');
        setGemPrices(response.data);
        setPricesError(null);
      } catch (error) {
        console.error("젬 시세 로딩 실패:", error);
        setPricesError("실시간 젬 시세를 불러오는 데 실패했습니다.");
      } finally {
        setPricesLoading(false);
      }
    };
    fetchGemPrices();
  }, []); // 빈 배열을 전달하여 이 effect가 단 한 번만 실행되도록 함

  // --- Functions (기존과 동일) ---
  const addCore = () => { setCores([...cores, { id: nextCoreId, type: '질서', grade: '고대' }]); setNextCoreId(nextCoreId + 1); };
  const removeCore = (id) => setCores(cores.filter(core => core.id !== id));
  const handleCoreChange = (id, field, value) => { setCores(cores.map(core => core.id === id ? { ...core, [field]: value } : core)); };
  const addHeldGem = () => { setHeldGems([...heldGems, { id: nextGemId, name: '안정', core_point: 5, efficiency: 5 }]); setNextGemId(nextGemId + 1); };
  const removeHeldGem = (id) => setHeldGems(heldGems.filter(gem => gem.id !== id));
  const handleHeldGemChange = (id, field, value) => { const parsedValue = (field === 'core_point' || field === 'efficiency') ? parseInt(value, 10) : value; setHeldGems(heldGems.map(gem => gem.id === id ? { ...gem, [field]: parsedValue } : gem)); };
  const handleCrystalChange = (event, newValue) => setCrystalPrice(newValue);
  const handleSimulationsChange = (event, newValue) => setSimulations(newValue);
  const handleOptimizeClick = async () => {
    const formattedCores = { "질서": cores.filter(c => c.type === '질서').map(c => c.grade), "혼돈": cores.filter(c => c.type === '혼돈').map(c => c.grade) };
    const formattedHeldGems = heldGems.map(({ id, name, ...rest }) => { const isOrderGem = ["안정", "견고", "불변"].includes(name); const prefix = isOrderGem ? "질서의 젬 : " : "혼돈의 젬 : "; return { ...rest, name: `${prefix}${name}` }; });
    const requestData = { cores: formattedCores, held_gems: formattedHeldGems, blue_crystal_price: crystalPrice, simulations_per_gem: simulations };
    try {
      const response = await axios.post('http://localhost:8000/optimize', requestData);
      const { task_id } = response.data;
      if (task_id) { setTaskId(task_id); setPageState('loading'); } 
      else { alert("작업 ID를 받아오는데 실패했습니다."); }
    } catch (error) { console.error("API 요청 실패:", error); alert("최적화 작업을 시작하지 못했습니다. 서버 상태를 확인해주세요."); }
  };
  
  // <<< 5. 젬 시세 데이터를 테이블로 렌더링하는 헬퍼 함수
  const renderPriceTable = (title, filterKeyword) => (
    <TableContainer component={Paper}>
      <Typography variant="h6" sx={{ p: 2 }}>{title}</Typography>
      <Table size="small">
        <TableHead>
          <TableRow>
            <TableCell>아이템 이름</TableCell>
            <TableCell align="right">최저가 (Gold)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {Object.entries(gemPrices)
            .filter(([name]) => name.includes(filterKeyword))
            .sort() // 이름순 정렬
            .map(([name, price]) => (
              <TableRow key={name}>
                <TableCell>{name}</TableCell>
                <TableCell align="right">{price ? price.toLocaleString() : 'N/A'}</TableCell>
              </TableRow>
            ))}
        </TableBody>
      </Table>
    </TableContainer>
  );

  return (
    <div>
      {/* <<< 6. 젬 시세 대시보드 UI */}
      <Box sx={{ marginBottom: 4 }}>
        <Typography variant="h5" gutterBottom>실시간 젬 시세</Typography>
        {pricesLoading && <CircularProgress />}
        {pricesError && <Typography color="error">{pricesError}</Typography>}
        {gemPrices && (
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              {renderPriceTable('질서의 젬', '질서')}
            </Grid>
            <Grid item xs={12} md={6}>
              {renderPriceTable('혼돈의 젬', '혼돈')}
            </Grid>
          </Grid>
        )}
      </Box>

      <hr />

      {/* --- 입력 폼 (기존과 동일) --- */}
      <h2 style={{marginTop: '30px'}}>1. 코어 구성</h2>
      {/* ... (이하 JSX 코드는 기존과 동일) ... */}
      <Box sx={{ marginBottom: 2 }}>{cores.map((core) => (<Box key={core.id} sx={{ display: 'flex', alignItems: 'center', marginBottom: 1 }}><FormControl sx={{ m: 1, minWidth: 120 }}><InputLabel>타입</InputLabel><Select value={core.type} label="타입" onChange={(e) => handleCoreChange(core.id, 'type', e.target.value)}><MenuItem value="질서">질서</MenuItem><MenuItem value="혼돈">혼돈</MenuItem></Select></FormControl><FormControl sx={{ m: 1, minWidth: 120 }}><InputLabel>등급</InputLabel><Select value={core.grade} label="등급" onChange={(e) => handleCoreChange(core.id, 'grade', e.target.value)}><MenuItem value="고대">고대</MenuItem><MenuItem value="유물">유물</MenuItem></Select></FormControl><IconButton aria-label="delete" onClick={() => removeCore(core.id)}><DeleteIcon /></IconButton></Box>))}</Box>
      <Button variant="contained" onClick={addCore}>+ 코어 추가</Button>
      <h2 style={{marginTop: '30px'}}>2. 보유 젬 (선택)</h2>
      <Box sx={{ marginBottom: 2 }}>{heldGems.map((gem) => (<Box key={gem.id} sx={{ display: 'flex', alignItems: 'center', marginBottom: 1 }}><FormControl sx={{ m: 1, minWidth: 120 }}><InputLabel>젬 타입</InputLabel><Select value={gem.name} label="젬 타입" onChange={(e) => handleHeldGemChange(gem.id, 'name', e.target.value)}>{gemTypes.map(type => <MenuItem key={type} value={type}>{type}</MenuItem>)}</Select></FormControl><TextField sx={{ m: 1, width: '100px' }} label="코어 포인트" type="number" value={gem.core_point} onChange={(e) => handleHeldGemChange(gem.id, 'core_point', e.target.value)} InputProps={{ inputProps: { min: 1, max: 5 } }} /><TextField sx={{ m: 1, width: '100px' }} label="효율" type="number" value={gem.efficiency} onChange={(e) => handleHeldGemChange(gem.id, 'efficiency', e.target.value)} InputProps={{ inputProps: { min: 1, max: 5 } }} /><IconButton aria-label="delete" onClick={() => removeHeldGem(gem.id)}><DeleteIcon /></IconButton></Box>))}</Box>
      <Button variant="contained" onClick={addHeldGem}>+ 보유 젬 추가</Button>
      <h2 style={{marginTop: '30px'}}>3. 시장 및 설정</h2>
      <Box sx={{ width: 300, margin: 'auto' }}><Typography gutterBottom>블루 크리스탈 시세: {crystalPrice.toLocaleString()} Gold</Typography><Slider value={crystalPrice} onChange={handleCrystalChange} step={100} marks min={2000} max={20000} /><Box sx={{ marginTop: '20px' }}><Typography gutterBottom>정밀도 (시뮬레이션 횟수): {simulations} 회</Typography><Typography variant="caption" color="text.secondary">(높을수록 계산 시간이 오래 걸립니다)</Typography><Slider value={simulations} onChange={handleSimulationsChange} step={50} marks min={50} max={1000} /><Chip label={`예상 시간: ${getEstimatedTime(simulations)}`} color="info" variant="outlined" size="small" /></Box></Box>
      <hr style={{ marginTop: '30px' }}/>
      <Button variant="contained" color="success" onClick={handleOptimizeClick} sx={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}>최적화 시작하기</Button>
    </div>
  );
};

// getEstimatedTime 헬퍼 함수 추가
const getEstimatedTime = (sims) => {
  if (sims <= 200) return "약 1분 이내";
  if (sims <= 500) return "약 1~3분";
  if (sims <= 800) return "약 3~5분";
  return "5분 이상";
};

export default InputPage;
