// src/pages/InputPage.js (코어 구성 UI 적용 버전)
import React, { useState } from 'react';
import { Box, Typography, Slider, Button, IconButton, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete'; // 삭제 아이콘

const InputPage = ({ setPageState, setResultData }) => {
  // 코어 목록을 관리하는 state. 각 코어는 고유한 id를 가집니다.
  const [cores, setCores] = useState([]);
  const [nextCoreId, setNextCoreId] = useState(1); // 고유 ID 생성을 위한 카운터

  const [crystalPrice, setCrystalPrice] = useState(8000);
  const [simulations, setSimulations] = useState(100);

  // '+ 코어 추가' 버튼 클릭 시 호출될 함수
  const addCore = () => {
    setCores([...cores, { id: nextCoreId, type: '질서', grade: '고대' }]);
    setNextCoreId(nextCoreId + 1); // 다음 ID 준비
  };

  // 'X' 삭제 버튼 클릭 시 호출될 함수
  const removeCore = (idToRemove) => {
    setCores(cores.filter(core => core.id !== idToRemove));
  };

  // 코어의 타입 또는 등급이 변경될 때 호출될 함수
  const handleCoreChange = (id, field, value) => {
    setCores(cores.map(core =>
      core.id === id ? { ...core, [field]: value } : core
    ));
  };


  const handleCrystalChange = (event, newValue) => {
    setCrystalPrice(newValue);
  };

  const handleSimulationsChange = (event, newValue) => {
    setSimulations(newValue);
  };

  const handleOptimizeClick = () => {
    // API로 보낼 데이터 형식에 맞게 코어 데이터를 가공
    const formattedCores = {
      "질서": cores.filter(c => c.type === '질서').map(c => c.grade),
      "혼돈": cores.filter(c => c.type === '혼돈').map(c => c.grade),
    };

    const requestData = {
      cores: formattedCores,
      held_gems: [], // 보유 젬 기능은 다음 단계에서 구현
      blue_crystal_price: crystalPrice,
      simulations_per_gem: simulations,
    };
    console.log("API 요청 데이터:", requestData);

    setPageState('loading');
    setTimeout(() => {
      setResultData({ total_cost: 123456 });
      setPageState('result');
    }, 2000);
  };

  return (
    <div>
      <h2>1. 코어 구성</h2>
      {/* 동적으로 생성될 코어 목록 */}
      <Box sx={{ marginBottom: 2 }}>
        {cores.map((core) => (
          <Box key={core.id} sx={{ display: 'flex', alignItems: 'center', marginBottom: 1 }}>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <InputLabel>타입</InputLabel>
              <Select
                value={core.type}
                label="타입"
                onChange={(e) => handleCoreChange(core.id, 'type', e.target.value)}
              >
                <MenuItem value="질서">질서</MenuItem>
                <MenuItem value="혼돈">혼돈</MenuItem>
              </Select>
            </FormControl>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <InputLabel>등급</InputLabel>
              <Select
                value={core.grade}
                label="등급"
                onChange={(e) => handleCoreChange(core.id, 'grade', e.target.value)}
              >
                <MenuItem value="고대">고대</MenuItem>
                <MenuItem value="유물">유물</MenuItem>
              </Select>
            </FormControl>
            <IconButton aria-label="delete" onClick={() => removeCore(core.id)}>
              <DeleteIcon />
            </IconButton>
          </Box>
        ))}
      </Box>
      <Button variant="contained" onClick={addCore}>+ 코어 추가</Button>

      <h2 style={{marginTop: '30px'}}>2. 보유 젬 (선택)</h2>
      <Button variant="contained">+ 보유 젬 추가</Button>

      <h2 style={{marginTop: '30px'}}>3. 시장 및 설정</h2>
      <Box sx={{ width: 300, margin: 'auto' }}>
        <Typography id="crystal-slider" gutterBottom>
          블루 크리스탈 시세: {crystalPrice.toLocaleString()} Gold
        </Typography>
        <Slider
          value={crystalPrice}
          onChange={handleCrystalChange}
          step={100}
          marks
          min={2000}
          max={20000}
        />
        <Typography id="simulations-slider" gutterBottom style={{ marginTop: '20px' }}>
          정밀도 (시뮬레이션 횟수): {simulations} 회
        </Typography>
        <Slider
          value={simulations}
          onChange={handleSimulationsChange}
          step={50}
          marks
          min={50}
          max={1000}
        />
      </Box>

      <hr style={{ marginTop: '30px' }}/>

      <Button variant="contained" color="success" onClick={handleOptimizeClick} sx={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}>
        최적화 시작하기
      </Button>
    </div>
  );
};

export default InputPage;
