// src/pages/InputPage.js (보유 젬 UI 적용 버전)
import React, { useState } from 'react';
import { Box, Typography, Slider, Button, IconButton, Select, MenuItem, FormControl, InputLabel, TextField } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

// 젬 타입 목록 (나중에 더 추가될 수 있음)
const gemTypes = ["안정", "견고", "불변", "침식", "왜곡", "붕괴"];

const InputPage = ({ setPageState, setResultData }) => {
  // --- State Hooks ---
  const [cores, setCores] = useState([]);
  const [nextCoreId, setNextCoreId] = useState(1);
  
  const [heldGems, setHeldGems] = useState([]);
  const [nextGemId, setNextGemId] = useState(1);

  const [crystalPrice, setCrystalPrice] = useState(8000);
  const [simulations, setSimulations] = useState(100);

  // --- Core Functions ---
  const addCore = () => {
    setCores([...cores, { id: nextCoreId, type: '질서', grade: '고대' }]);
    setNextCoreId(nextCoreId + 1);
  };
  const removeCore = (id) => setCores(cores.filter(core => core.id !== id));
  const handleCoreChange = (id, field, value) => {
    setCores(cores.map(core => core.id === id ? { ...core, [field]: value } : core));
  };

  // --- Held Gem Functions ---
  const addHeldGem = () => {
    setHeldGems([...heldGems, { id: nextGemId, name: '안정', core_point: 5, efficiency: 5 }]);
    setNextGemId(nextGemId + 1);
  };
  const removeHeldGem = (id) => setHeldGems(heldGems.filter(gem => gem.id !== id));
  const handleHeldGemChange = (id, field, value) => {
    // 숫자 필드는 숫자로 변환
    const parsedValue = (field === 'core_point' || field === 'efficiency') ? parseInt(value, 10) : value;
    setHeldGems(heldGems.map(gem => gem.id === id ? { ...gem, [field]: parsedValue } : gem));
  };

  // --- Slider Handlers ---
  const handleCrystalChange = (event, newValue) => setCrystalPrice(newValue);
  const handleSimulationsChange = (event, newValue) => setSimulations(newValue);

  // --- Main Action ---
  const handleOptimizeClick = () => {
    const formattedCores = {
      "질서": cores.filter(c => c.type === '질서').map(c => c.grade),
      "혼돈": cores.filter(c => c.type === '혼돈').map(c => c.grade),
    };
    
    // 보유 젬 데이터 가공 (id를 제외하고, name에 타입 접두사 추가)
    const formattedHeldGems = heldGems.map(({ id, name, ...rest }) => {
        const isOrderGem = ["안정", "견고", "불변"].includes(name);
        const prefix = isOrderGem ? "질서의 젬 : " : "혼돈의 젬 : ";
        return {
            ...rest,
            name: `${prefix}${name}`
        };
    });

    const requestData = {
      cores: formattedCores,
      held_gems: formattedHeldGems,
      blue_crystal_price: crystalPrice,
      simulations_per_gem: simulations,
    };
    console.log("API 요청 데이터:", JSON.stringify(requestData, null, 2));

    setPageState('loading');
    setTimeout(() => {
      setResultData({ total_cost: 123456 });
      setPageState('result');
    }, 2000);
  };

  return (
    <div>
      <h2>1. 코어 구성</h2>
      <Box sx={{ marginBottom: 2 }}>
        {cores.map((core) => (
          <Box key={core.id} sx={{ display: 'flex', alignItems: 'center', marginBottom: 1 }}>
            {/* ... 코어 UI (기존과 동일) ... */}
            <FormControl sx={{ m: 1, minWidth: 120 }}><InputLabel>타입</InputLabel><Select value={core.type} label="타입" onChange={(e) => handleCoreChange(core.id, 'type', e.target.value)}><MenuItem value="질서">질서</MenuItem><MenuItem value="혼돈">혼돈</MenuItem></Select></FormControl>
            <FormControl sx={{ m: 1, minWidth: 120 }}><InputLabel>등급</InputLabel><Select value={core.grade} label="등급" onChange={(e) => handleCoreChange(core.id, 'grade', e.target.value)}><MenuItem value="고대">고대</MenuItem><MenuItem value="유물">유물</MenuItem></Select></FormControl>
            <IconButton aria-label="delete" onClick={() => removeCore(core.id)}><DeleteIcon /></IconButton>
          </Box>
        ))}
      </Box>
      <Button variant="contained" onClick={addCore}>+ 코어 추가</Button>

      <h2 style={{marginTop: '30px'}}>2. 보유 젬 (선택)</h2>
      <Box sx={{ marginBottom: 2 }}>
        {heldGems.map((gem) => (
          <Box key={gem.id} sx={{ display: 'flex', alignItems: 'center', marginBottom: 1 }}>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <InputLabel>젬 타입</InputLabel>
              <Select value={gem.name} label="젬 타입" onChange={(e) => handleHeldGemChange(gem.id, 'name', e.target.value)}>
                {gemTypes.map(type => <MenuItem key={type} value={type}>{type}</MenuItem>)}
              </Select>
            </FormControl>
            <TextField sx={{ m: 1, width: '100px' }} label="코어 포인트" type="number" value={gem.core_point} onChange={(e) => handleHeldGemChange(gem.id, 'core_point', e.target.value)} InputProps={{ inputProps: { min: 1, max: 5 } }} />
            <TextField sx={{ m: 1, width: '100px' }} label="효율" type="number" value={gem.efficiency} onChange={(e) => handleHeldGemChange(gem.id, 'efficiency', e.target.value)} InputProps={{ inputProps: { min: 1, max: 5 } }} />
            <IconButton aria-label="delete" onClick={() => removeHeldGem(gem.id)}><DeleteIcon /></IconButton>
          </Box>
        ))}
      </Box>
      <Button variant="contained" onClick={addHeldGem}>+ 보유 젬 추가</Button>

      <h2 style={{marginTop: '30px'}}>3. 시장 및 설정</h2>
      {/* ... 슬라이더 UI (기존과 동일) ... */}
      <Box sx={{ width: 300, margin: 'auto' }}><Typography gutterBottom>블루 크리스탈 시세: {crystalPrice.toLocaleString()} Gold</Typography><Slider value={crystalPrice} onChange={handleCrystalChange} step={100} marks min={2000} max={20000} /><Typography gutterBottom style={{ marginTop: '20px' }}>정밀도 (시뮬레이션 횟수): {simulations} 회</Typography><Slider value={simulations} onChange={handleSimulationsChange} step={50} marks min={50} max={1000} /></Box>

      <hr style={{ marginTop: '30px' }}/>

      <Button variant="contained" color="success" onClick={handleOptimizeClick} sx={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}>
        최적화 시작하기
      </Button>
    </div>
  );
};

export default InputPage;
